css:
@page { size: A4; margin: 10mm; }
  body {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
    margin: 0;
    padding: 0;
    -webkit-print-color-adjust: exact;
  }
  .page {
    width: 210mm;
    min-height: 297mm;
    margin: auto;
    background: #fff;
    padding: 8mm;
    box-sizing: border-box;
  }
  .header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-bottom: 2px solid #000;
    padding-bottom: 5px;
  }
  .header img {
    width: 80px;
    height: 60px;
    object-fit: contain;
    border: 1px solid #000;
  }
  .header-center {
    flex: 1;
    text-align: center;
  }
  .header-center h1 {
    color: #0a2b6f;
    font-size: 24px;
    font-weight: bold;
    margin: 0;
  }
  .header-center h2 {
    font-size: 14px;
    margin: 0;
    border: 1px solid #000;
    display: inline-block;
    padding: 3px 10px;
  }
  .header-right img {
    width: 60px;
    height: 60px;
    object-fit: contain;
    border: 1px solid #000;
  }
  .details, .requirements, .process, .inspection, .dispatch {
    border: 1px solid #000;
    margin-top: 6px;
    padding: 5px;
  }
  table {
    width: 100%;
    border-collapse: collapse;
  }
  th, td {
    border: 1px solid #000;
    padding: 4px;
    font-size: 11px;
    vertical-align: top !important;
  }
  th {
    background: #e6f0ff;
    text-align: center;
  }
  .double {
    display: flex;
    gap: 5px;
  }
  .half {
    flex: 1;
    display: flex;
    flex-direction: column;
  }
  .half .image-space {
    margin-top: auto;
    align-self: flex-end;
  }
  .large-box {
    height: 180px;
    border: 1px solid #000;
    margin-top: 5px;
  }
  .large-area {
    height: 250px;
    border: 1px solid #000;
    margin-top: 5px;
    position: relative;
  }
  .signature {
    display: flex;
    justify-content: space-between;
    margin-top: 10px;
  }
  .image-space {
    width: 100%;
    height: 200px;
    text-align: center;
    font-size: 11px;
    color: #555;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 5px;
  }
